mapper.select:
SELECT BOARD_NO
FROM BOARD_TAG_TBL
WHERE BOARD_TAG_NAME IN

  #{tag}

GROUP BY BOARD_NO
HAVING COUNT(DISTINCT BOARD_TAG_NAME) = #{tagCount}

select.if:
SELECT * FROM BOARD_TBL WHERE BOARD_NO IN
#{boardNo}
ORDER BY BOARD_DATE DESC

SELECT * FROM BOARD_TBL WHERE BOARD_NO IN
#{boardNo}
ORDER BY VIEW_COUNT DESC

SELECT * FROM BOARD_TBL WHERE BOARD_NO IN
#{boardNo}
ORDER BY BOARD_POINT DESC
