mapper.select:
SELECT * FROM
(SELECT LIQUOR_ID, LIQUOR_NAME, ALCHOL_CONTENT, LIQUOR_TYPE, LIQUOR_PRICE, LIQUOR_CAPACITY, L_CREATE_DATE, L_UPDATE_DATE, BREWERY_ID, BREWERY_NAME, BREWERY_LOCAL
FROM LIQUOR_TBL
LEFT OUTER JOIN BREWERY_TBL
	ON LIQUOR_TBL.BREWERY_ID = BREWERY_TBL.BREWERY_NO


			AND LIQUOR_NAME LIKE '%'||#{sInfo.keyword}||'%'


			AND BREWERY_LOCAL = #{sInfo.breweryLocal}


			AND LIQUOR_TYPE = #{sInfo.liquorType}


			AND ALCHOL_CONTENT >= #{sInfo.alcholMin}


			AND ALCHOL_CONTENT <= #{sInfo.alcholMax}


			AND LIQUOR_CAPACITY >= #{sInfo.capacityMin}


			AND LIQUOR_CAPACITY <= #{sInfo.capacityMax}


			AND LIQUOR_PRICE >= #{sInfo.priceMin}


			AND LIQUOR_PRICE <= #{sInfo.priceMax}


		AND LIQUOR_ID IN
		(SELECT LIQUOR_ID
		FROM LIQUOR_TBL L
		LEFT OUTER JOIN (
			SELECT BOARD_TAG_NAME, LIQUOR_ID
			FROM BOARD_TAG_TBL
			LEFT OUTER JOIN BOARD_TBL
			USING(BOARD_NO)
		) T
		USING(LIQUOR_ID)
		WHERE
				T.BOARD_TAG_NAME IN

					#{tag}

		GROUP BY LIQUOR_ID
			HAVING COUNT(BOARD_TAG_NAME) >= #{tagLength}
		)


) A
LEFT OUTER JOIN
(SELECT LIQUOR_ID, AVG(BOARD_POINT) AVG_POINT, COUNT(*) REVIEW_COUNT FROM BOARD_TBL GROUP BY LIQUOR_ID) B
USING(LIQUOR_ID)
